mapper.insert:
insert into OPERATELOG (OPERATEID, USERID, OPERATENAME, 
  OBJECTID, OPERATEDESC, OPERATETIME
  )
values (#{operateid,jdbcType=DECIMAL}, #{userid,jdbcType=VARCHAR}, #{operatename,jdbcType=VARCHAR}, 
  #{objectid,jdbcType=VARCHAR}, #{operatedesc,jdbcType=VARCHAR}, #{operatetime,jdbcType=DATE}
  )

mapper.select:
  		select * from (
 
   select s.*,rownum r from (select l.* from OPERATELOG l ) s where 1=1 
  	
and OperateTime between  to_date(#{startDate},'yyyy-mm-dd') and  to_date(#{endDate},'yyyy-mm-dd') 
	
    
  		and OperateTime=to_date(#{date},'yyyy-mm-dd')
  	 
  	) aa where 1=1 
 	
  		 and aa.r between #{start} and #{end}
